function [sk,f] = bc_ful(numnp,npbc,bfvec,sk,f)%applies boundary conditionsfor i=1:1:numnp                                                                                                                             n1=npbc(i,1);      n2=npbc(i,2);      n3=npbc(i,3);            i3=3*i;      i2=i3-1;      i1=i3-2;	        if(n1==1)                                              	  	  sk(i1,i1)  =sk(i1,i1) *1.0e+5;                             f(i1)      =bfvec(i,1)*sk(i1,i1);                                                    else      f(i1)      =f(i1)  +bfvec(i,1);                                                end	        if(n2==1)                                              	  	  sk(i2,i2)  =sk(i2,i2) *1.0e+5;                             f(i2)      =bfvec(i,2)*sk(i2,i2);                                                    else      f(i2)      =f(i2)  +bfvec(i,2);                                                end	        if(n3==1)                                              	  	  sk(i3,i3)  =sk(i3,i3) *1.0e+5;                             f(i3)      =bfvec(i,3)*sk(i3,i3);                                                    else      f(i3)      =f(i3)  +bfvec(i,3);                                                end	  end                                                               