function   [bigNsurf] = bigNsurfmat(nnps,nqpts,ssfac)% compute bigNs matrix %bigNsurfpost = zeros(6,6*nnps,nqpts);bigNsurf = zeros(6,6*nnps,nqpts);%bigNsurfpre = zeros(3,6*nnps,nqpts);Z=zeros(1,nnps);for i=1:1:nqpts             N=zeros(1,nnps);    for j=1:1:nnps         for k=1:6%            bigNsurfpost(k,(k-1)*nnps+j,i) = ssfac(j,i);            bigNsurf(k,(k-1)*nnps+j,i) = ssfac(j,i);        end              N(j)=ssfac(j,i);        end %   bigNsurfpre(:,:,i)=[  N Z Z N Z N;%                         Z N Z N N Z;%                         Z Z N Z N N];%    bigNsurfpre(:,:,i)=[ N N N N N N;%                         N N N N N N;%                         N N N N N N];end 