function zmatrix = con_result (numel,numnp,meltyp,nnpe,nqptv,wtq,sfac,dndxi,dndet,np,x,y,zdata)%  driver for consistent resultant computation	  skx = zeros(numnp,numnp);fx = zeros(numnp);zmatrix = zeros(numnp);for   iele =1:1:numel	  [dndx,dndy,detj] = sfder(iele,nnpe,nqptv,dndxi,dndet,np,x,y);  ses = elcrs(nnpe,nqptv,wtq,detj,sfac);  fes = elcrf(iele,nnpe,nqptv,wtq,detj,sfac,zdata);  [skx,fx] = asscr_ful(iele,nnpe,np,ses,fes,skx,fx);endzmatrix = fx/skx;