function ses = elcrs(nnpe,nqptv,wtq,detj,sfac)% forms elemental stiffness matrix for 4 or 8 node elementses = zeros(nnpe,nnpe);                                                                     for   k=1:1:nqptv                                                                           xkfac=wtq(k)*detj(k);                                                                       for  i=1:1:nnpe	      for  j=1:1:nnpe                                                                           ses(i,j)=ses(i,j)+xkfac*sfac(i,k)*sfac(j,k);                                                                         end	    end                                                                     end