function se = elstif(iele,nnpe,nqptv,wtq,bigB,detj,r)% forms elemental stiffness matrix for 4 or 8 node elementiend=3*nnpe;se = zeros(iend,iend);                                                               for   k=1:1:nqptv       bigBqp = bigB(:,:,k);                                                                         xkfac(k)=wtq(k)*detj(k);      se = se + xkfac(k)*(bigBqp' * r * bigBqp);      end