function r = mater_lib(gmod,xlam)r = zeros(6,6);      xb=gmod/((1.-2.*xlam)*(1.+xlam)) ;                                 r(1,1)=xb*(1.-xlam) ;                                                 r(1,2)=xb*xlam ;                                                   r(1,3)=r(1,2) ;                                                    r(2,1)=r(1,2) ;                                                          r(2,2)=r(1,1) ;                                                          r(2,3)=r(1,2) ;                                                    r(3,1)=r(1,2) ;                                                          r(3,2)=r(1,2) ;                                                          r(3,3)=r(1,1) ;                                                        r(4,4)=xb*(1.-2.*xlam)/2.;                                                r(5,5)=xb*(1.-2.*xlam)/2.;                                                r(6,6)=xb*(1.-2.*xlam)/2.;                                                                        