function outdat(numnp,nqptv,u,signp,epsnp)% output of solutionfid = 0;while fid<1	filename = input('Output file name for displacement: ', 's');	[fid,message] = fopen(filename,'w');	if(fid==-1)		disp(message);	endendfor ifor =1:1:numnp	i3 = 3*ifor;    i2 = i3-1;	i1 = i2-1;	fprintf(fid,'%10.7e  %10.7e %10.7e \n',u(i1),u(i2),u(i3));endstatus = fclose(fid);% fid = 0;% while fid<1% 	filename = input('Output file name for stress: ', 's');% 	[fid,message] = fopen(filename,'w');% 	if(fid==-1)% 		disp(message);% 	end% end% % for ifor =1:1:numnp% 		fprintf(fid,'%10.7e \n', signp(ifor));% end% 	% status = fclose(fid);% % % fid = 0;% while fid<1% 	filename = input('Output file name for strain: ', 's');% 	[fid,message] = fopen(filename,'w');% 	if(fid==-1)% 		disp(message);% 	end% end% % for ifor =1:1:numnp% 		fprintf(fid,'%10.7e \n', epsnp(ifor));% end% % status = fclose(fid);