function [gmod,xlam,gamvec,numbc,npbc,ganvec,bfvec,surtracvec] = propbc_data(numnp,numel,numels)% input of problem data% the variables numel and numels are never useddum = 'true'; fid = 0;while fid<1	filename = input('Input data file name: ', 's');	[fid,message] = fopen(filename,'r');	if(fid==-1)		disp(message);    endendgmod = fscanf(fid,'%lf',1);if(gmod<=0.0) 	error('gmod must be greater than zero')endxlam = fscanf(fid,'%lf',1);if(xlam<0.0) 	error('xlam must be greater than zero')endgamx = fscanf(fid,'%lf',1);if(gamx<0.0) 	error('gamx must be greater than zero')endgamy = fscanf(fid,'%lf',1);if(gamy<0.0) 	error('gamy must be greater than zero')endgamz = fscanf(fid,'%lf',1);if(gamz<0.0) 	error('gamy must be greater than zero')endnumbc = fscanf(fid,'%d',1);if(numbc<=0.0) 	error('numbc must be greater than zero')endgamvec(1)= gamx;gamvec(2)= gamy;gamvec(3)= gamz;bfx = zeros(numnp,1);bfy = zeros(numnp,1);bfz = zeros(numnp,1); bfvec = zeros(numnp,3); ganglx = zeros(numnp,1);gangly = zeros(numnp,1);ganglz = zeros(numnp,1);ganvec = zeros(numnp,3);npbc = zeros(numnp,3);for ifor = 1:1:numbc	bc_info = fscanf(fid,'%d',4);	f_info = fscanf(fid,'%lf',6);	i1=bc_info(1);	npbc(i1,1) = bc_info(2);	npbc(i1,2) = bc_info(3);	npbc(i1,3) = bc_info(4);	bfx(i1)= f_info(1);	bfy(i1) = f_info(2);    bfz(i1) = f_info(3);	ganglx(i1) = f_info(4);	gangly(i1) = f_info(5);	ganglz(i1) = f_info(6);endfor ifor = 1:1:numnp  bfvec(ifor,1) = bfx(ifor);  bfvec(ifor,2) = bfy(ifor);  bfvec(ifor,3) = bfz(ifor);  ganvec(ifor,1) = ganglx(ifor);  ganvec(ifor,2) = gangly(ifor);  ganvec(ifor,3) = ganglz(ifor);end  % Correct numsurfssurtracvec = zeros(numels,3);nsurftracdata = fscanf(fid,'%d',1);	  if(nsurftracdata~=0)	  for  ifor =1:1:nsurftracdata       	  bcs_info = fscanf(fid,'%d',1);      i1 = bcs_info;      fs_info = fscanf(fid,'%lf',3);	      surtracvec(i1,1)= fs_info(1);	      surtracvec(i1,2)= fs_info(2);	      surtracvec(i1,3)= fs_info(3);endendstatus = fclose(fid);a = 1;