function  si = ssxi(xi,i,meltyp)% computes derivative of shape function wrt xi      if(meltyp==6)		  switch i            case 1				si=4.*xi-3.;			case 2				si=4.-8.*xi;			case 3				si=4.*xi-1.;			end                                                   		end	      if(meltyp==8)		  switch i            case 1				si=4.*xi-3.;			case 2				si=4.-8.*xi;			case 3				si=4.*xi-1.;			end                                                   		end		      if(meltyp==9)		  switch i            case 1				si=4.*xi-3.;			case 2				si=4.-8.*xi;			case 3				si=4.*xi-1.;			end                                                   		end		      if(meltyp==4)          switch i		    case 1			    si=-1.0;                                                     		    case 2    			    si=1.0;			end      end		      if(meltyp==3)          switch i		    case 1			    si=-1.0;                                                     		    case 2    			    si=1.0;			end      end