function [Ksr,Ksc,Ksv,kc] = assmbl_ful(iele,nnpe,np,se,Ksr,Ksc,Ksv,kc)% assembles elemental force and stiffness matrices into global counterpartsfor  i=1:1:nnpe        i6=6*np(iele,i);    i5=i6-1;    i4=i5-1;    i3=i4-1;    i2=i3-1;    i1=i2-1;        for j=1:1:nnpe                j6=6*np(iele,j);        j5=j6-1;        j4=j5-1;        j3=j4-1;        j2=j3-1;        j1=j2-1;                Ksr(kc)=i1;        Ksc(kc)=j1;        Ksv(kc)=se(i,j);        kc=kc+1;        Ksr(kc)=i1;        Ksc(kc)=j2;        Ksv(kc)=se(i,j+nnpe);        kc=kc+1;        Ksr(kc)=i1;        Ksc(kc)=j3;        Ksv(kc)=se(i,j+2*nnpe);        kc=kc+1;        Ksr(kc)=i1;        Ksc(kc)=j4;        Ksv(kc)=se(i,j+3*nnpe);        kc=kc+1;        Ksr(kc)=i1;        Ksc(kc)=j5;        Ksv(kc)=se(i,j+4*nnpe);        kc=kc+1;        Ksr(kc)=i1;        Ksc(kc)=j6;        Ksv(kc)=se(i,j+5*nnpe);        kc=kc+1;                Ksr(kc)=i2;        Ksc(kc)=j1;        Ksv(kc)=se(i+nnpe,j);        kc=kc+1;        Ksr(kc)=i2;        Ksc(kc)=j2;        Ksv(kc)=se(i+nnpe,j+nnpe);        kc=kc+1;        Ksr(kc)=i2;        Ksc(kc)=j3;        Ksv(kc)=se(i+nnpe,j+2*nnpe);        kc=kc+1;        Ksr(kc)=i2;        Ksc(kc)=j4;        Ksv(kc)=se(i+nnpe,j+3*nnpe);        kc=kc+1;        Ksr(kc)=i2;        Ksc(kc)=j4;        Ksv(kc)=se(i+nnpe,j+4*nnpe);        kc=kc+1;        Ksr(kc)=i2;        Ksc(kc)=j5;        Ksv(kc)=se(i+nnpe,j+5*nnpe);        kc=kc+1;                        Ksr(kc)=i3;        Ksc(kc)=j1;        Ksv(kc)=se(i+2*nnpe,j);        kc=kc+1;        Ksr(kc)=i3;        Ksc(kc)=j2;        Ksv(kc)=se(i+2*nnpe,j+nnpe);        kc=kc+1;        Ksr(kc)=i3;        Ksc(kc)=j3;        Ksv(kc)=se(i+2*nnpe,j+2*nnpe);        kc=kc+1;        Ksr(kc)=i3;        Ksc(kc)=j4;        Ksv(kc)=se(i+2*nnpe,j+3*nnpe);        kc=kc+1;        Ksr(kc)=i3;        Ksc(kc)=j5;        Ksv(kc)=se(i+2*nnpe,j+4*nnpe);        kc=kc+1;        Ksr(kc)=i3;        Ksc(kc)=j6;        Ksv(kc)=se(i+2*nnpe,j+5*nnpe);        kc=kc+1;                Ksr(kc)=i4;        Ksc(kc)=j1;        Ksv(kc)=se(i+3*nnpe,j);        kc=kc+1;        Ksr(kc)=i4;        Ksc(kc)=j2;        Ksv(kc)=se(i+3*nnpe,j+nnpe);        kc=kc+1;        Ksr(kc)=i4;        Ksc(kc)=j3;        Ksv(kc)=se(i+3*nnpe,j+2*nnpe);        kc=kc+1;        Ksr(kc)=i4;        Ksc(kc)=j4;        Ksv(kc)=se(i+3*nnpe,j+3*nnpe);        kc=kc+1;        Ksr(kc)=i4;        Ksc(kc)=j5;        Ksv(kc)=se(i+3*nnpe,j+4*nnpe);        kc=kc+1;        Ksr(kc)=i4;        Ksc(kc)=j6;        Ksv(kc)=se(i+3*nnpe,j+5*nnpe);        kc=kc+1;                        Ksr(kc)=i5;        Ksc(kc)=j1;        Ksv(kc)=se(i+4*nnpe,j);        kc=kc+1;        Ksr(kc)=i5;        Ksc(kc)=j2;        Ksv(kc)=se(i+4*nnpe,j+nnpe);        kc=kc+1;        Ksr(kc)=i5;        Ksc(kc)=j3;        Ksv(kc)=se(i+4*nnpe,j+2*nnpe);        kc=kc+1;        Ksr(kc)=i5;        Ksc(kc)=j4;        Ksv(kc)=se(i+4*nnpe,j+3*nnpe);        kc=kc+1;        Ksr(kc)=i5;        Ksc(kc)=j5;        Ksv(kc)=se(i+4*nnpe,j+4*nnpe);        kc=kc+1;        Ksr(kc)=i5;        Ksc(kc)=j6;        Ksv(kc)=se(i+4*nnpe,j+5*nnpe);        kc=kc+1;                Ksr(kc)=i6;        Ksc(kc)=j1;        Ksv(kc)=se(i+5*nnpe,j);        kc=kc+1;        Ksr(kc)=i6;        Ksc(kc)=j2;        Ksv(kc)=se(i+5*nnpe,j+nnpe);        kc=kc+1;        Ksr(kc)=i6;        Ksc(kc)=j3;        Ksv(kc)=se(i+5*nnpe,j+2*nnpe);        kc=kc+1;        Ksr(kc)=i6;        Ksc(kc)=j4;        Ksv(kc)=se(i+5*nnpe,j+3*nnpe);        kc=kc+1;        Ksr(kc)=i6;        Ksc(kc)=j5;        Ksv(kc)=se(i+5*nnpe,j+4*nnpe);        kc=kc+1;        Ksr(kc)=i6;        Ksc(kc)=j6;        Ksv(kc)=se(i+5*nnpe,j+5*nnpe);        kc=kc+1;            end    end