function ft=fet(q1,q2,n,meltyp) % shape function derivative wrt xi% checked by eralpif(meltyp==27) 		    switch n      case 1      ft = (2.0*q1-1.0)*(4.0*q2-3.0)*(q1-1.0);      case 2      ft = 4.0*q1*(1.0-q1)*(4.0*q2-3.0);      case 3      ft = q1*(2.0*q1-1.0)*(4.0*q2-3.0);      case 4      ft = 4.0*q1*(2.0*q1-1.0)*(1.0-2.0*q2);      case 5      ft = q1*(2.0*q1-1.0)*(4.0*q2-1.0);      case 6      ft = 4.0*q1*(1.0-q1)*(4.0*q2-1.0);      case 7      ft = (q1-1.0)*(2.0*q1-1.0)*(4.0*q2-1.0);      case 8      ft = 4.0*(2.0*q1-1.0)*(q1-1.0)*(1.0-2.0*q2);      case 9      ft = 16.0*q1*(1.0-q1)*(1.0-2.0*q2);                                end end% checked by eralpif(meltyp==20) 		    switch n   case 1                                  ft=(1.0-q1)*(2.0*q1+4.0*q2-3.0);      case 2                                                               ft=4.0*q1*(q1-1.0);          case 3                                                              ft=q1*(4.0*q2-2.0*q1-1.0);       case 4                                                               ft=4.0*q1*(1.0-2.0*q2);    case 5                                                              ft=q1*(4.0*q2+2.0*q1-3.0);   case 6                                                               ft=4.0*q1*(1.0-q1);        case 7                                                               ft=(q1-1.0)*(1.0+2.0*q1-4.0*q2);      case 8   ft=4.0*(1.0-q1)*(1.0-2.0*q2);                                       end end% checked by eralpif(meltyp==10) 		      switch n    case 3      ft=0.0;	    case 4      ft=4.0*q1;	    case 5      ft=4.0*q2-1.0;	    case 6      ft=4.0*(1.0-q1-2.0*q2);		    case 1      ft=-3.0+4.0*q1+4.0*q2;	    case 2      ft=-4.0*q1;	    endend% checked by eralpif(meltyp==8)		    switch n  case 1  ft=q1-1.0;  case 2  ft=-q1;  case 4  ft=1.0-q1;  case 3  ft=q1;  end      end                                                                         